mapper.select:
SELECT member_idx
	FROM member
	WHERE member_id = #{sId}

SELECT *
	FROM wish
	WHERE product_idx = #{product_idx}
		AND member_idx = #{member_idx}

SELECT image_main_file, image_real_file1, image_real_file2  
	FROM image 
	WHERE product_idx = #{product_idx}

SELECT distinct(product_color)
	FROM product
	WHERE product_name = #{product.product_name}

mapper.insert:

	SELECT IFNULL(MAX(order_idx),0)
	FROM orderlist


INSERT
	INTO orderlist 
	VALUES (
		#{order_idx} + 1
		,now()
		,#{order_price}
		,'주문완료'
		,'결제완료'
		,#{member_idx}
		,#{product_idx}
		,#{order_product_name}
		,#{order_count}
		,#{order_size}
		,#{order_color}
	)


	SELECT MAX(order_idx) FROM orderlist


INSERT
	INTO order_detail
	VALUES(
		#{order_idx}
		,#{order.member_idx}
		,#{order.product_idx}
		,#{delivery.order_name}
		,#{delivery.order_phone}
		,CONCAT(#{delivery.order_addr1},",",#{delivery.order_addr2})
		,#{delivery.order_content}
	)

mapper.update:
UPDATE product
	SET
		product_amount = product_amount - #{order_count}
		, product_sell_count = product_sell_count + #{order_count}
	WHERE product_idx = #{product_idx}

mapper.select:
SELECT COUNT(*)
	FROM orderlist
	WHERE member_idx = #{member_idx}
